    name: "",
    imageCard: "",
    imageDetail: "",
    height: "",
    weight: "",
    baseExp: "",
    hp: "",
    types: [],
    attack: "",
    defense: "",
    speed: "",
  });

  let [button, setButton] = useState(true);

  useEffect(() => {
    setButton(buttonHab(errors));
  }, [errors]);

